    private void btnCalcularActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCalcularActionPerformed
        String E, M,H,L,V;
        E = txtNumeros.getText( );
        L = null;
        String[] N = E.split(",");
        for (String numero : N) {
           double valor = Double.parseDouble(numero.trim());          
           double raizCuadrada = Math.sqrt(valor);
           double cuadrado = valor * valor;
           double cubo = valor * valor * valor;         
           M = "La ra√≠z cuadrada de " + valor + " es " + raizCuadrada + "\n" +
                 "El cuadrado de " + valor + " es " + cuadrado + "\n" +
                 "El cubo de " + valor + " es " + cubo  + "\n";           
           txtRespuesta.setText(M);
           H = txtRespuesta.getText() + L;
           L = H;
           
        }
        txtRespuesta.setText(L);
    }//GEN-LAST:event_btnCalcularActionPerformed

